target.echo:
#!/bin/sh # chkconfig: 345 99 01 # description: ${distro.name} startup script # runcommand=${basedir}/../${distro.name}.sh runuser=$(/usr/bin/whoami) echo Running as $runuser if [ $runuser != "cfml" ]; then #su - cfml --session-command="/home/cfml/myapp/myapp.sh $1" runcommand="sudo -u cfml -i -H $runcommand" echo Switching to cfml user fi case "$1" in
start) echo "starting ${distro.name}" shift $runcommand server.start @* ;;
stop) echo "stopping ${distro.name}" shift $runcommand server.stop @* ;;
restart) echo "restarting ${distro.name}" shift $runcommand server.restart @* ;;
*) echo "*** Usage: ${distro.name} {start | stop | restart}" exit 1 esac
